mapper.select:
select id, login_id, password, name, admin_yn
  from "user"
 where id = #{id}

select id, login_id, password, name, admin_yn
  from "user"
 where login_id = #{login_id}

mapper.insert:
insert into "user" (
    login_id, password, name
)
values (
    #{login_id}
    , #{password}
    , #{name}
)

    select id
      from "user"
     where login_id = #{login_id}

mapper.select:
select count(*)
from user_question
where user_id = #{user_id}
