mapper.insert:
insert into question (
    text, image_name, image, seq, use_yn, type, category_id
)
values (
    #{text}
    ,#{image_name}
    ,#{image}
    ,#{seq}
    ,#{use_yn}
    ,#{type}
    ,#{category_id}
    )

mapper.update:
update question
set text = #{text}
    , image_name = #{image_name}
    , image = #{image}
    , seq = #{seq}
    , use_yn = #{use_yn}
    , type = #{type}
    , category_id =#{category_id}
where id = #{id}

update question
set text = #{text}
  , seq = #{seq}
  , use_yn = #{use_yn}
  , type = #{type}
  , category_id =#{category_id}
where id = #{id}

mapper.insert:
insert into question_option (
    number, seq, text, correct_yn, del_yn, question_id
) values (
    #{number}
    ,#{seq}
    ,#{text}
    ,#{correct_yn}
    ,#{del_yn}
    ,#{question_id}
    )

mapper.update:
update question_option
set number = #{number}
    , seq = #{seq}
    , text = #{text}
    , correct_yn = #{correct_yn}
    , del_yn = #{del_yn}
    , question_id = #{question_id}
where id = #{id}

update category
set name = #{name}
  , description = #{description}
  , logo_url = #{logo_url}
  , time_limit = #{time_limit}
  , question_cnt = #{question_cnt}
  , success_type = #{success_type}
  , success_cnt = #{success_cnt}
  , success_percent = #{success_percent}
  , use_yn = #{use_yn}
  , active_yn = #{active_yn}
  , seq = #{seq}
where id = #{id}

mapper.insert:
insert into category (
    p_id, name, description, time_limit, question_cnt, logo_url,
    success_type, success_cnt, success_percent, use_yn, active_yn, seq
) values (
    #{p_id}, #{name}, #{description}, #{time_limit}, #{question_cnt}, #{logo_url},
    #{success_type}, #{success_cnt}, #{success_percent}, #{use_yn}, #{active_yn}, #{seq}
)

mapper.select:
SELECT COUNT(*)
FROM question q
WHERE q.category_id = #{categoryId}
